import axios from "axios";
export default {
  name: "OpinionGraph",
  components: {
    GChart,
    PartyInfo
  },
  props: {
    currentView: String
  },
  methods: {
    grabData() {
      let returnList;
      axios.get("https://docs.google.com/spreadsheets/d/e/2PACX-1vSHdxj1KPFMHP5gT1ybNfd_-N_7QikiUQ1zMKJgGb4xZjwPY4DcRE6xjWHlWqgbhawjk8ZO6I52MakV/pubhtml").then(response => returnList = this.parseData(response.data));
      return returnList;
    },
    parseData(data) {
      var returnData = [["Day", "CPC", "LPC", "NDP", "BQ", "PPC", "GPC"]];
      var dataCap = returnData[0].length;
      var indexCounter = 0;
      data = data.substring(data.indexOf("Mapoll : Federal"));
      console.log("novo");
      var table = data.split(/<.*?>/);
      for (var item of table) {
        //remove non-data
        // if (item.includes("/") || item.length == 0) {
        //   const index = table.indexOf(item);
        //   if (index > -1) {
        //     table.splice(index, 1);
        //   }
        // }
        if (!item.includes("/") && !item.length == 0) {
          if (item.charAt(0) == "[" && item.charAt(item.length - 1) == "]") {
            indexCounter = 0;
            var newArray = new Array();
            newArray.push(table[table.indexOf(item) - 2]);
            returnData.push(newArray);
          } else if (indexCounter < dataCap && returnData.length > 1) {
            returnData[returnData.length - 1].push(this.toInt(item));
            indexCounter++;
          }
        }
      }
      console.log(returnData);
      return returnData;
    },
    toInt(value) {
      value.replaceAll("*", "");
      console.log(value);
      return parseInt(value);
    }
  },
  mounted() {},
  data() {
    // let dataList = [
    //   ["Day", "CPC", "LPC", "NDP", "BQ", "PPC", "GPC"],
    //   [1, 35, 30, 20, 7, 3, 3],
    //   [2, 37, 30, 20, 7, 1, 3],
    //   [3, 40.8, 32.7, 12.4, 5.2, 3.4, 2.6],
    //   [4, 33, 29, 23, 6, 2, 5],
    //   [5, 33, 22, 21, 6, 2, 5],
    //   [6, 33, 31, 21, 8, 2, 3],
    //   [7, 35, 34, 18, 6, 3, 5],
    // ];
    let dataList = this.grabData();
    let partyList = [];
    let partyNums = new Map();
    for (let p of dataList[0]) {
      if (p !== "Day" && !partyList.includes(p)) {
        partyList.push(p);
        partyNums.set(p, dataList[dataList.length - 1][dataList[0].indexOf(p)]);
      }
    }
    return {
      data: dataList,
      partyList: partyList,
      partyNums: partyNums,
      options: {
        backgroundColor: "#e8e8e8",
        focusTarget: "category",
        animation: {
          duration: 1000,
          easing: "out"
        },
        curveType: "function",
        chart: {
          title: "Federal Polls",
          subtitle: "In percentage"
        },
        width: 1000,
        height: 400
      }
    };
  }
};
